import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const OwnerView = ({ storeId }) => {
  const [forecast, setForecast] = useState(null);
  const [insights, setInsights] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [forecastRes, insightsRes] = await Promise.all([
          axios.get(`/stores/${storeId}/forecast`),
          axios.get(`/owner/${storeId}/insights`)
        ]);
        setForecast(forecastRes.data);
        setInsights(insightsRes.data);
      } catch (error) {
        console.error("Error fetching owner data:", error);
      }
      setLoading(false);
    };

    fetchData();
  }, [storeId]);

  if (loading) return <div style={{ textAlign: 'center', marginTop: '2rem' }}>Loading Dashboard...</div>;

  const chartData = forecast ? {
    labels: forecast.ds,
    datasets: [
      {
        label: 'Sales Forecast',
        data: forecast.yhat,
        borderColor: '#3b82f6',
        backgroundColor: 'rgba(59, 130, 246, 0.5)',
        tension: 0.4,
      },
      {
        label: 'Lower Bound',
        data: forecast.yhat_lower,
        borderColor: 'rgba(255, 255, 255, 0.1)',
        borderDash: [5, 5],
        pointRadius: 0,
        fill: false,
      },
      {
        label: 'Upper Bound',
        data: forecast.yhat_upper,
        borderColor: 'rgba(255, 255, 255, 0.1)',
        borderDash: [5, 5],
        pointRadius: 0,
        fill: false,
      }
    ],
  } : null;

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
        labels: { color: '#94a3b8' }
      },
      title: {
        display: true,
        text: '7-Day Sales Forecast',
        color: '#f8fafc',
        font: { size: 16 }
      },
    },
    scales: {
      y: {
        grid: { color: 'rgba(255, 255, 255, 0.05)' },
        ticks: { color: '#94a3b8' }
      },
      x: {
        grid: { display: false },
        ticks: { color: '#94a3b8' }
      }
    }
  };

  return (
    <div className="owner-dashboard" style={{ display: 'flex', flexDirection: 'column', gap: '2rem' }}>

      {/* Insights Section */}
      <div className="insights-grid" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1.5rem' }}>
        {insights.map((insight, idx) => (
          <div key={idx} className="glass-card">
            <h3 style={{ color: 'var(--text-muted)', fontSize: '0.9rem', textTransform: 'uppercase', letterSpacing: '1px' }}>
              {insight.kpi}
            </h3>
            <div style={{ fontSize: '2rem', fontWeight: 'bold', margin: '0.5rem 0', color: 'var(--primary)' }}>
              {insight.value}
            </div>
            <p style={{ fontSize: '0.95rem', color: 'var(--text-main)', opacity: 0.9 }}>
              {insight.recommendation}
            </p>
          </div>
        ))}
      </div>

      {/* Forecast Chart */}
      <div className="glass-card" style={{ padding: '2rem' }}>
        {chartData && <Line options={chartOptions} data={chartData} />}
      </div>
    </div>
  );
};

export default OwnerView;
